#!/usr/bin/python
d = ""

def isDividedByThreeStr(str4):
    """Return True if sum of each digits in the str4 is divided by 3
    >>> isDividedByThreeStr("123")
    True
    >>> isDividedByThreeStr("1234")
    False
    >>> isDividedByThreeStr("1046")
    False
    """
    digitsSum = 0
    for c in str4:
        digitsSum = digitsSum + int(c)
    if digitsSum % 3 == 0:
        return True
    return False

def main():
    count = 0
    for i in range(len(d) - 3):
        if (isDividedByThreeStr(d[i:i+4])):
            count = count + 1
    print(count)

if __name__ == "__main__":
    import doctest
    doctest.testmod()
    main()
